### plot functions ###

plot_results <- function(
  dense300.train, dense300.valid,
  dense1000.train, dense1000.valid,
  dense300.100.train, dense300.100.valid,
  lenet1.train, lenet1.valid,
  lenet5.train, lenet5.valid,
  title,
  position
) {
  y_min = min(c(
    min(dense300.train),
    min(dense300.valid),
    min(dense1000.train),
    min(dense1000.valid),
    min(dense300.100.train),
    min(dense300.100.valid),
    min(lenet1.train),
    min(lenet1.valid),
    min(lenet5.train),
    min(lenet5.valid))
  )
  
  par(mfrow=c(1,2))
  
  plot(1:100, dense300.train, col = 1, pch = ".",
       ylim = c(y_min, 1), xlab = "iteration", ylab = "accuracy",
       main = "acc. over iterations")
  points(1:100, dense300.valid, col = 1, type = "l")
  points(1:100, dense1000.train, col = 2, pch = ".")
  points(1:100, dense1000.valid, col = 2, type = "l")
  points(1:100, dense300.100.train, col = 3, pch = ".")
  points(1:100, dense300.100.valid, col = 3, type = "l")
  points(1:100, lenet1.train, col = 4, pch = ".")
  points(1:100, lenet1.valid, col = 4, type = "l")
  points(1:100, lenet5.train, col = 5, pch = ".")
  points(1:100, lenet5.valid, col = 5, type = "l")
  
  legend(position,
         c("dense300",
           "dense1000",
           "dense300-100",
           "lenet1",
           "lenet5"),
         col = c(1, 2, 3, 4, 5),
         text.col = c(1, 2, 3, 4, 5),
         pch = c("-", "-", "-", "-", "-"))
  
  plot(dense300.train, dense300.valid, col = 1, pch = "+",
       xlim = c(y_min, 1), ylim = c(y_min, 1),
       main = "valid acc. over train acc.",
       xlab = "training accuracy", ylab = "validation accuracy")
  points(dense1000.train, dense1000.valid, col = 2, pch = "+")
  points(dense300.100.train, dense300.100.valid, col = 3, pch = "+")
  points(lenet1.train, lenet1.valid, col = 4, pch = "+")
  points(lenet5.train, lenet5.valid, col = 5, pch = "+")
  
  title(main = title, outer = TRUE)
  par(mfrow=c(1,1))
}


