  zip.dense300.train, zip.dense300.valid,
  zip.dense1000.train, zip.dense1000.valid,
  zip.dense300.100.train, zip.dense300.100.valid,
  zip.lenet1.train, zip.lenet1.valid,
  zip.lenet5.train, zip.lenet5.valid,
  "ZIP dataset",
  "bottomright"
)


